//https://www.hackerrank.com/challenges/java-1d-arrayimport java.io.*;import java.util.*;import java.text.*;import java.math.*;import java.util.regex.*;public class Solution {    static int m;    static int n;    static boolean res = false;    static boolean was[];    public static void main(String[] args) {        Scanner in = new Scanner(System.in);        int testCases = in.nextInt();        while(testCases-- > 0){            res = false;            n = in.nextInt();            m = in.nextInt();            int[] arr = new int[100];            was = new boolean[100];            for(int i = 0; i<n ; i++){                arr[i] = in.nextInt();            }            rec(arr,0);            if(res){System.out.println("YES");}            else{System.out.println("NO");}        }    }        public static void rec(int[] array, int i){        if(res || was[i]){return;}        //        System.out.println(i + " " + res + " " + m);        was[i]=true;        if(i+m >= n || i+1 >= n){            //            System.out.println("res = true");            res = true;            return;        }if(i>0 && array[i-1] == 0){            rec(array,i-1);        }if(array[i+1] == 0){            rec(array,i+1);        }if(array[i+m] == 0){            rec(array,i+m);        }            }}